"use strict";(self.webpackChunk_yuants_docs=self.webpackChunk_yuants_docs||[]).push([[5974],{6967:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>i,contentTitle:()=>c,default:()=>l,frontMatter:()=>a,metadata:()=>o,toc:()=>d});var t=r(9541),s=r(4317);const a={},c="Providing Transfer Interface",o={id:"vendor-guide/vendor-transfer",title:"Providing Transfer Interface",description:"You may need to read Basics / Transfer to deeply understand the basic concepts of transfers in Yuan.",source:"@site/docs/vendor-guide/vendor-transfer.md",sourceDirName:"vendor-guide",slug:"/vendor-guide/vendor-transfer",permalink:"/docs/vendor-guide/vendor-transfer",draft:!1,unlisted:!1,editUrl:"https://github.com/No-Trade-No-Life/Yuan/tree/main/ui/docs/docs/vendor-guide/vendor-transfer.md",tags:[],version:"current",frontMatter:{},sidebar:"docsSidebar",previous:{title:"Providing Trading Interface",permalink:"/docs/vendor-guide/vendor-trading-interfaces"},next:{title:"Protocol",permalink:"/docs/category/protocol"}},i={},d=[];function u(e){const n={a:"a",admonition:"admonition",code:"code",h1:"h1",p:"p",pre:"pre",...(0,s.a)(),...e.components};return(0,t.jsxs)(t.Fragment,{children:[(0,t.jsx)(n.h1,{id:"providing-transfer-interface",children:"Providing Transfer Interface"}),"\n",(0,t.jsx)(n.admonition,{type:"info",children:(0,t.jsxs)(n.p,{children:["You may need to read ",(0,t.jsx)(n.a,{href:"/docs/basics/what-is-transfer-order",children:"Basics / Transfer"})," to deeply understand the basic concepts of transfers in Yuan."]})}),"\n",(0,t.jsx)(n.p,{children:"Vendors are responsible for implementing the transfer interface, which is used for transferring funds between accounts."}),"\n",(0,t.jsxs)(n.p,{children:["Implementing the transfer interface is straightforward; simply use the ",(0,t.jsx)(n.code,{children:"addAccountTransferAddress"})," method to register the account's transfer and account checking functions."]}),"\n",(0,t.jsxs)(n.p,{children:["Specifically, the transfer route is uniquely determined by the quadruple (",(0,t.jsx)(n.code,{children:"account ID"}),", ",(0,t.jsx)(n.code,{children:"transfer network ID"}),", ",(0,t.jsx)(n.code,{children:"currency"}),", ",(0,t.jsx)(n.code,{children:"address"}),"), which is known as ",(0,t.jsx)(n.a,{href:"/docs/basics/what-is-transfer-order#account-address-information",children:"Account Address Information"}),"."]}),"\n",(0,t.jsxs)(n.p,{children:["A single running vendor instance may provide multiple ways of transferring and checking accounts. Suppose you want to implement a vendor that provides two sets of transfers, namely (",(0,t.jsx)(n.code,{children:"account_id_1"}),", ",(0,t.jsx)(n.code,{children:"USDT"}),", ",(0,t.jsx)(n.code,{children:"AccountInternal/1/SubAccount/1"}),", ",(0,t.jsx)(n.code,{children:"main"}),") and (",(0,t.jsx)(n.code,{children:"account_id_2"}),", ",(0,t.jsx)(n.code,{children:"USDT"}),", ",(0,t.jsx)(n.code,{children:"TRC20"}),", ",(0,t.jsx)(n.code,{children:"0x1234567890"}),"). You can implement it as follows:"]}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{className:"language-ts",children:"import { ITerminal, addAccountTransferAddress } from '@yuants/protocol';\nimport { ITransferOrder } from '@yuants/transfer';\n\nconst terminal = new ITerminal(process.env.HOST_URL!, {});\n\naddAccountTransferAddress({\n  terminal,\n  account_id: 'account_id_1', // Account ID\n  currency: 'USDT', // Currency\n  network_id: 'AccountInternal/1/SubAccount/1', // Transfer network ID, here we are describing a main account-subaccount transfer.\n  address: 'main', // Address, for AccountInternal/1/SubAccount/1, this can be 'main' or the main account ID, as long as it is distinct from the subaccount's address\n  onApply: {\n    INIT: async (order: ITransferOrder) => {\n      /// NOTE: makeSubAccountParams and Api.transferSubAccount need to be implemented by yourself\n      const params = makeSubAccountParams(order);\n      const transferResult = await Api.transferSubAccount(params);\n      if (!transferResult.success) {\n        /// NOTE: All states other than COMPLETE/ERROR will be sent back to the current step executor by the transfer controller, such as returning INIT here, the transfer controller will set the transfer order state to INIT and resend it to the current vendor's step for execution, until success or transfer timeout.\n        return { state: 'INIT', message: transferResult.message };\n      }\n      return { state: 'COMPLETE' };\n    },\n  },\n  /// NOTE: For such transfers, we assume they will be completed immediately, so we directly return COMPLETE\n  onEval: async (order: ITransferOrder) => {\n    return { state: 'COMPLETE' };\n  },\n});\n\naddAccountTransferAddress({\n  terminal,\n  account_id: 'account_id_2',\n  currency: 'USDT',\n  network_id: 'TRC20', // Here we describe a TRC20 transfer\n  address: '0x123456789', // The TRC20 address of the account must be provided here\n  onApply: {\n    INIT: async (order: ITransferOrder) => {\n      /// NOTE: makeTRC20Params and Api.transferTRC20 need to be implemented by yourself\n      const params = makeCheckTRC20Params(order);\n      const transferResult = await Api.transferTRC20(params);\n      if (!transferResult.success) {\n        return { state: 'INIT', message: transferResult.message };\n      }\n      const withdrawId = transferResult.withdrawId;\n      /// NOTE: Sometimes transfers cannot be completed immediately, such as TRC20 transfers needing to wait for chain confirmation,\n      ///   until the chain Transaction ID is obtained, we consider the transfer step to be over,\n      ///   at which point we need to let the current transfer step enter a new state (any name, here we call it AWAIT_TX_ID) and return a context information,\n      ///   then the transfer controller will save this context information in the current_tx_context field of the transfer order and resend it to the corresponding step of the current vendor for execution.\n      return { state: 'AWAIT_TX_ID', context: withdrawId };\n    },\n    AWAIT_TX_ID: async (order: ITransferOrder) => {\n      const withdrawId = order.current_tx_context;\n      /// NOTE: Api.getWithdrawHistory needs to be implemented by yourself\n      const withdrawHistoryResult = await Api.getWithdrawHistory(withdrawId);\n      const transaction_id = withdrawHistoryResult?.transactionId;\n      if (!transaction_id) {\n        return { state: 'AWAIT_TX_ID', context: withdrawId };\n      }\n      return { state: 'COMPLETE', transaction_id };\n    },\n  },\n  onEval: async (order: ITransferOrder) => {\n    /// NOTE: makeCheckTRC20Params and Api.checkTRC20 need to be implemented by yourself\n    const params = makeCheckTRC20Params(order);\n    const checkResult = await Api.checkTRC20(params);\n    if (!checkResult.success) {\n      return { state: 'INIT', message: checkResult.message };\n    }\n    const received_amount = checkResult.receivedAmount;\n    return { state: 'COMPLETE', received_amount };\n  },\n});\n"})}),"\n",(0,t.jsxs)(n.p,{children:["The above example provides a simple implementation of the transfer interface. For a real-world example, please see: ",(0,t.jsx)(n.a,{href:"https://github.com/No-Trade-No-Life/Yuan/blob/4dc37b9c30292a2fd87a311cca3d06f9e53e4f2d/apps/vendor-okx/src/index.ts#L521",children:"OKX Transfer"}),"."]}),"\n",(0,t.jsx)(n.h1,{id:"appendix-underlying-implementation-of-the-transfer-interface",children:"Appendix: Underlying Implementation of the Transfer Interface"}),"\n",(0,t.jsxs)(n.admonition,{title:"You may not need to read this section",type:"info",children:[(0,t.jsxs)(n.p,{children:["The following example is only used to demonstrate what the actual transfer interface looks like behind the ",(0,t.jsx)(n.code,{children:"addAccountTransferAddress"})," method."]}),(0,t.jsx)(n.p,{children:"You may not need to read this section unless you need to deeply understand the underlying implementation of transfers in Yuan."})]}),"\n",(0,t.jsxs)(n.p,{children:["The transfer interface is defined through Yuan's ",(0,t.jsx)(n.a,{href:"/docs/protocol/message-pattern-layer#client-server-mode",children:"Client/Server Mode"})," and consists of two APIs: transfer request and transfer query."]}),"\n",(0,t.jsx)(n.p,{children:"Vendors need to implement the transfer request and transfer query for the current transfer step based on the execution fields in the transfer order."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{className:"language-ts",children:"interface IService {\n  // Initiate transfer\n  TransferApply: {\n    req: ITransferOrder;\n    res: IResponse<{ state: string; context?: string; transaction_id?: string; message?: string }>;\n    frame: void;\n  };\n  // Verify transfer (account checking)\n  TransferEval: {\n    req: ITransferOrder;\n    res: IResponse<{ state: string; context?: string; received_amount?: number } | void>;\n    frame: void;\n  };\n}\n"})}),"\n",(0,t.jsx)(n.p,{children:"The maintainer of the vendor needs to register the implementation of these two APIs in the Terminal."}),"\n",(0,t.jsxs)(n.p,{children:["As in the example in the ",(0,t.jsx)(n.a,{href:"/docs/vendor-guide/vendor-transfer#providing-transfer-interface",children:"Guide"}),", a vendor may need to provide multiple account address information transfer interfaces, so it is necessary to determine which logical branch the current transfer should go to based on the execution fields in the specific transfer order."]}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{className:"language-ts",children:"import { ITerminal } from '@yuants/protocol';\n\nconst terminal = new ITerminal(process.env.HOST_URL!, {});\n\nconst contextList = {\n  {\n    account_id: 'account_id_1',\n    currency: 'USDT',\n    network_id: 'AccountInternal/1/SubAccount/1',\n    address: 'main',\n  },\n  {\n    account_id: 'account_id_2',\n    currency: 'USDT',\n    network_id: 'TRC20',\n    address: '0x1234567890',\n  },\n};\n\nterminal.server.provideService(\n  'TransferApply',\n  {\n    type: 'object',\n    required: ['current_tx_account_id', 'currency', 'current_network_id', 'current_tx_address'],\n    oneOf: contextList.map((x) => ({\n      properties: {\n        current_tx_account_id: {\n          const: x.account_id,\n        },\n        currency: {\n          const: x.currency,\n        },\n        current_network_id: {\n          const: x.network_id,\n        },\n        current_tx_address: {\n          const: x.address,\n        },\n      },\n    })),\n  },\n  async (req) => {\n    const { current_tx_account_id, currency, current_network_id, current_tx_address, current_tx_state } = req;\n    if (current_tx_account_id === 'account_id_1' && currency === 'USDT' && current_network_id === 'AccountInternal/1/SubAccount/1' && current_tx_address === 'main') {\n      if (current_tx_state === 'INIT') {\n        /// NOTE: makeSubAccountParams and Api.transferSubAccount need to be implemented by yourself\n        const params = makeSubAccountParams(order);\n        const transferResult = await Api.transferSubAccount(params);\n        if (!transferResult.success) {\n          return { state: 'INIT', message: transferResult.message };\n        }\n        return { state: 'COMPLETE' };\n      }\n      return { res: { code: 400, message: 'Unknown State', data: { state: 'ERROR' } } };\n    } else if (current_tx_account_id === 'account_id_2' && currency === 'USDT' && current_network_id === 'TRC20' && current_tx_address === '0x1234567890') {\n        if (current_tx_state === 'INIT') {\n          /// NOTE: makeTRC20Params and Api.transferTRC20 need to be implemented by yourself\n          const params = makeTRC20Params(order);\n          const transferResult = await Api.transferTRC20(params);\n          if (!transferResult.success) {\n            return { state: 'INIT', message: transferResult.message };\n          }\n          const withdrawId = transferResult.withdrawId;\n          return { state: 'AWAIT_TX_ID', context: withdrawId };\n        }\n        if (current_tx_state === 'AWAIT_TX_ID') {\n          const withdrawId = order.current_tx_context;\n          const withdrawHistoryResult = await Api.getWithdrawHistory(withdrawId);\n          const transactionId = withdrawHistoryResult?.transactionId;\n          if (!transactionId) {\n              return { state: 'AWAIT_TX_ID', context: withdrawId };\n          }\n          return { state: 'COMPLETE', transaction_id: transactionId };\n        }\n      return { res: { code: 400, message: 'Unknown State', data: { state: 'ERROR' } } };\n    }\n    return { state: 'COMPLETE' };\n  },\n);\n\nterminal.server.provideService(\n  'TransferEval',\n  {\n    type: 'object',\n    required: ['current_rx_account_id', 'currency', 'current_network_id', 'current_rx_address'],\n    oneOf: contextList.map((x) => ({\n      properties: {\n        current_rx_account_id: {\n          const: x.account_id,\n        },\n        currency: {\n          const: x.currency,\n        },\n        current_network_id: {\n          const: x.network_id,\n        },\n        current_rx_address: {\n          const: x.address,\n        },\n      },\n    })),\n  },\n  async (req) => {\n    const { current_rx_account_id, currency, current_network_id, current_rx_address, current_rx_state } = req;\n    if (current_rx_account_id === '1' && currency === 'USDT' && current_network_id === 'AccountInternal/1/SubAccount/1' && current_rx_address === 'main') {\n      return { state: 'COMPLETE' };\n    }\n    if (current_rx_account_id === '2' && currency === 'USDT' && current_network_id === 'TRC20' && current_rx_address === '0x1234567890') {\n      /// NOTE: makeCheckTRC20Params and Api.checkTRC20 need to be implemented by yourself\n      const params = makeCheckTRC20Params(order);\n      const checkResult = await Api.checkTRC20(params);\n      if (!checkResult.success) {\n        return { state: 'INIT', message: checkResult.message };\n      }\n      const received_amount = checkResult.receivedAmount;\n      return { state: 'COMPLETE', received_amount };\n    }\n    return { state: 'COMPLETE' };\n  },\n);\n"})})]})}function l(e={}){const{wrapper:n}={...(0,s.a)(),...e.components};return n?(0,t.jsx)(n,{...e,children:(0,t.jsx)(u,{...e})}):u(e)}},4317:(e,n,r)=>{r.d(n,{Z:()=>o,a:()=>c});var t=r(3981);const s={},a=t.createContext(s);function c(e){const n=t.useContext(a);return t.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function o(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(s):e.components||s:c(e.components),t.createElement(a.Provider,{value:n},e.children)}}}]);