"use strict";(self.webpackChunk_yuants_docs=self.webpackChunk_yuants_docs||[]).push([[5151],{7130:n=>{n.exports=JSON.parse('{"blogPosts":[{"id":"vision-of-no-trade-no-life","metadata":{"permalink":"/zh-Hans/blog/vision-of-no-trade-no-life","editUrl":"https://github.com/No-Trade-No-Life/Yuan/tree/main/ui/docs/blog/2024-04-17-vision-of-no-trade-no-life.md","source":"@site/i18n/zh-Hans/docusaurus-plugin-content-blog/2024-04-17-vision-of-no-trade-no-life.md","title":"No Trade No Life \u7684\u613f\u666f","description":"\u6bcf\u4e2a\u4eba\u90fd\u53ef\u4ee5\u4f9d\u9760\u6295\u8d44\u4ea4\u6613\uff0c\u8f7b\u677e\u4eab\u53d7\u4e16\u754c\u7ecf\u6d4e\u53d1\u5c55\u6210\u679c\u3002","date":"2024-04-17T00:00:00.000Z","formattedDate":"2024\u5e744\u670817\u65e5","tags":[{"label":"Yuan","permalink":"/zh-Hans/blog/tags/yuan"}],"readingTime":3.645,"hasTruncateMarker":false,"authors":[{"name":"CZ","title":"NTNL Co-Founder","url":"https://github.com/zccz14","imageURL":"https://github.com/zccz14.png","key":"zccz14"}],"frontMatter":{"slug":"vision-of-no-trade-no-life","title":"No Trade No Life \u7684\u613f\u666f","authors":["zccz14"],"tags":["Yuan"]},"unlisted":false,"nextItem":{"title":"\u6211\u4eec\u771f\u7684\u9700\u8981 Python \u6765\u505a\u91cf\u5316\u4ea4\u6613\u5417\uff1f","permalink":"/zh-Hans/blog/do-we-really-need-python-for-quant"}},"content":"**\u6bcf\u4e2a\u4eba\u90fd\u53ef\u4ee5\u4f9d\u9760\u6295\u8d44\u4ea4\u6613\uff0c\u8f7b\u677e\u4eab\u53d7\u4e16\u754c\u7ecf\u6d4e\u53d1\u5c55\u6210\u679c**\u3002\\n\\n\u7ecf\u6d4e\u53d1\u5c55\u7684\u6700\u7ec8\u76ee\u7684\u662f\u63d0\u9ad8\u4eba\u6c11\u7684\u751f\u6d3b\u6c34\u51c6\u3002\\n\u4f9d\u9760\u6295\u8d44\u4ea4\u6613\uff0c\u4eab\u53d7\u7ecf\u6d4e\u53d1\u5c55\uff0c\u5c31\u662f\u4e3a\u4e86\u6539\u5584\u751f\u6d3b\uff0c\u8fd9\u5c31\u662f No Trade No Life \u7684\u672c\u4e49\u3002\\n\\n\u51fa\u4e8e\u907f\u514d\u4e3b\u9898\u7684\u8fc7\u5ea6\u6269\u5927\u3001\u5f15\u8d77\u4e89\u8bae\uff0c\u9996\u5148\u6392\u9664\u82e5\u5e72\u8bdd\u9898\uff1a\\n\\n1. \u6211\u4eec\u4e0d\u8003\u8651\u5e94\u5f53\u5982\u4f55\u89e3\u51b3\u8d2b\u5bcc\u5dee\u8ddd\u7684\u95ee\u9898\uff0c\u8fd9\u662f\u653f\u6cbb\u95ee\u9898\uff0c\u662f\u653f\u5e9c\u793e\u4f1a\u7ba1\u7406\u4eba\u5458\u9700\u8981\u8003\u8651\u7684\u8bfe\u9898\uff1b\\n2. \u6211\u4eec\u4e0d\u8003\u8651\u5e94\u5f53\u5982\u4f55\u76f4\u63a5\u4fc3\u8fdb\u7ecf\u6d4e\u7684\u53d1\u5c55\uff0c\u8fd9\u662f\u79d1\u5b66\u95ee\u9898\uff0c\u662f\u5404\u884c\u5404\u4e1a\u7684\u4eba\u624d\u4eec\u9700\u8981\u8003\u8651\u7684\u8bfe\u9898\uff1b\\n\\n\u6211\u4eec\u7ad9\u5728\u6bcf\u4e2a\u6295\u8d44\u8005\u7684\u89d2\u5ea6\u6765\u770b\uff0c\u601d\u8003\u5982\u4f55**\u79d1\u5b66\u9ad8\u6548**\u5730\u7ba1\u7406\u81ea\u5df1\u7684\u8d22\u5bcc\uff0c\u5e2e\u52a9\u6295\u8d44\u8005\u514b\u670d\u6295\u8d44\u8fc7\u7a0b\u4e2d\u4f1a\u9047\u5230\u7684\u56f0\u96be\u3002\\n\\n\u6211\u4eec\u60f3\u8981\u9610\u660e\u6295\u8d44\u7684\u56f0\u96be\u6027\uff0c\u5e76\u7ed9\u51fa\u9488\u5bf9\u6027\u7684\u89e3\u51b3\u65b9\u6848\uff0c\u4f7f\u5f97\u79d1\u6280\u80fd\u591f\u589e\u5f3a\u7528\u6237\u4e2a\u4eba\u7684\u80fd\u529b\uff0c\u4ece\u800c\u5e2e\u52a9\u7528\u6237\u66f4\u597d\u5730\u8fdb\u884c\u6295\u8d44\u64cd\u4f5c\u3002\\n\\n\u90a3\u4e48\uff0c\u6295\u8d44\u7684\u56f0\u96be\u5230\u5e95\u662f\u4ec0\u4e48\uff1f\\n\\n1. \u5e02\u573a\u4e0d\u505c\uff0c\u6295\u8d44\u592a\u5fd9 (\u8fc7\u91cf\u7684\u4fe1\u606f \u4e0e \u4f4e\u6548\u7684\u5904\u7406\u80fd\u529b \u4e4b\u95f4\u7684\u77db\u76fe)\uff1b\\n2. \u9700\u6c42\u591a\u6837\uff0c\u9009\u9879\u592a\u5c11 (\u591a\u6837\u5316\u7684\u6295\u8d44\u9700\u6c42 \u4e0e \u5c11\u91cf\u7684\u6295\u8d44\u54c1\u4f9b\u5e94 \u4e4b\u95f4\u7684\u77db\u76fe)\uff1b\\n3. \u60f3\u7684\u5f88\u7f8e\uff0c\u89c1\u8bc6\u592a\u5c11 (\u6295\u8d44\u8005\u7684\u9884\u671f \u4e0e \u6295\u8d44\u8005\u7684\u80fd\u529b \u4e0d\u5339\u914d\u7684\u77db\u76fe)\uff1b\\n4. \u60f3\u5f97\u5f53\u7136\uff0c\u89c9\u609f\u4e0d\u8db3 (\u6295\u8d44\u8005\u7684\u611f\u6027 \u4e0e \u6295\u8d44\u8005\u7684\u7406\u6027 \u4e4b\u95f4\u7684\u77db\u76fe)\\n\\n\u4e3a\u6b64\uff0c\u6211\u4eec\u9488\u5bf9\u4e0a\u8ff0\u95ee\u9898\uff0c\u5206\u522b\u63d0\u51fa\u9488\u5bf9\u6027\u7684\u89e3\u51b3\u65b9\u6848\uff1a\\n\\n1. \u5229\u7528\u5305\u62ec AI\u3001\u4e91\u8ba1\u7b97\u7b49\u4e00\u7cfb\u5217\u6280\u672f\uff0c\u5b9e\u73b0\u81ea\u52a8\u4ea4\u6613\uff0c\u89e3\u653e\u7528\u6237\u7684\u65f6\u95f4\u7cbe\u529b\uff0c\u89e3\u653e\u751f\u4ea7\u529b\u3002\\n2. \u7528\u6237\u53ef\u4ee5\u81ea\u884c\u4ece\u5e02\u573a\u6807\u51c6\u7684\u6295\u8d44\u54c1\u4e2d\uff0c\u642d\u914d\u590d\u5408\u7684\u6295\u8d44\u54c1\uff0c\u5efa\u7acb\u6295\u8d44\u7ec4\u5408\uff0c\u5efa\u7acb\u79c1\u52df\u57fa\u91d1\uff0c\u4ece\u800c\u6781\u5927\u4e30\u5bcc\u6295\u8d44\u54c1\uff0c\u4ee5\u6ee1\u8db3\u81ea\u8eab\u548c\u5468\u8fb9\u670b\u53cb\u7684\u6295\u8d44\u9700\u6c42\u3002\\n3. \u4ece\u5386\u53f2\u4e2d\u627e\u6559\u8bad\uff0c\u4ece\u77e5\u8bc6\u4e2d\u627e\u7ecf\u9a8c\u3002\u4e30\u5bcc\u6d4b\u8bd5\u4f53\u7cfb\u3001\u5e76\u642d\u5efa\u6295\u8d44\u4ea4\u6613\u77e5\u8bc6\u5e93\uff0c\u5e2e\u52a9\u5c0f\u767d\u4e00\u6b65\u6b65\u6210\u4e3a\u4e13\u4e1a\u6295\u8d44\u8005\uff0c\u5e76\u5e2e\u52a9\u6240\u6709\u6295\u8d44\u8005\u7ba1\u7406\u81ea\u8eab\u7684\u9884\u671f\u3002\\n4. \u901a\u8fc7\u6570\u636e\u53ef\u89c6\u5316\uff0c\u5c06\u67af\u71e5\u7684\u6570\u636e\uff0c\u8f6c\u5316\u4e3a\u53ef\u4ee5\u901a\u8fc7\u89c6\u89c9\u76f4\u63a5\u523a\u6fc0\u7528\u6237\u611f\u6027\u601d\u7ef4\u7684\u683c\u5f0f\uff0c\u5e2e\u52a9\u7528\u6237\u6446\u8131\u60f3\u5f53\u7136\u7684\u601d\u7ef4\u60f0\u6027\u3002\\n\\n\u5982\u679c\u7ec8\u6709\u4e00\u5929\u7ecf\u6d4e\u53d1\u5c55\u7684\u613f\u666f\u5f97\u4ee5\u5b9e\u73b0\uff0c\u4eba\u6c11\u7684\u751f\u6d3b\u6c34\u5e73\u5f97\u4ee5\u63d0\u9ad8\uff0c\u53ef\u80fd\u662f\u56e0\u4e3a:\\n\\n1. \u5982\u679c\u6295\u8d44\u4f9d\u7136\u91cd\u8981\uff0c\u90a3\u4e48\u6bcf\u4e2a\u4eba\u90fd\u6709\u4e00\u5957\u597d\u7528\u7684\u6295\u8d44\u64cd\u4f5c\u7cfb\u7edf\u3002(\u6211\u5e0c\u671b\u90a3\u4f1a\u662f\u6211\u4eec\u51fa\u54c1\u7684 Yuan)\\n2. \u5982\u679c\u6295\u8d44\u5df2\u7ecf\u4e0d\u518d\u91cd\u8981\uff0c\u90a3\u4e48\u5171\u4ea7\u4e3b\u4e49\u5927\u62b5\u5df2\u7ecf\u5b9e\u73b0\u4e86\u3002"},{"id":"do-we-really-need-python-for-quant","metadata":{"permalink":"/zh-Hans/blog/do-we-really-need-python-for-quant","editUrl":"https://github.com/No-Trade-No-Life/Yuan/tree/main/ui/docs/blog/2023-11-17-do-we-really-need-python-for-quant.md","source":"@site/i18n/zh-Hans/docusaurus-plugin-content-blog/2023-11-17-do-we-really-need-python-for-quant.md","title":"\u6211\u4eec\u771f\u7684\u9700\u8981 Python \u6765\u505a\u91cf\u5316\u4ea4\u6613\u5417\uff1f","description":"\u5982\u4eca\uff0cPython \u662f\u91cf\u5316\u4ea4\u6613\u4e2d\u6700\u6d41\u884c\u7684\u7f16\u7a0b\u8bed\u8a00\u3002VNPY\u3001Zipline\u3001PyAlgoTrade \u548c Backtrader \u90fd\u662f\u7528 Python \u7f16\u5199\u7684\u3002Python \u5728\u91cf\u5316\u4ea4\u6613\u4e2d\u88ab\u5e7f\u6cdb\u4f7f\u7528\uff0c\u4f46\u53ef\u80fd\u5e76\u975e\u6700\u9002\u5408\u8fd9\u9879\u5de5\u4f5c\u7684\u8bed\u8a00\u3002\u867d\u7136\u5b83\u6709\u5f88\u591a\u4f18\u70b9\uff0c\u6bd4\u5982\u6613\u4e8e\u5b66\u4e60\u548c\u62e5\u6709\u51fa\u8272\u7684\u6570\u636e\u79d1\u5b66\u751f\u6001\u7cfb\u7edf\uff0c\u4f46\u91cf\u5316\u6295\u8d44\u5e73\u53f0\u5982 Quantopian\u3001JoinQuant \u548c QuantConnect \u5374\u96be\u4ee5\u76c8\u5229\uff0c\u5c3d\u7ba1\u4e92\u8054\u7f51\u57fa\u7840\u8bbe\u65bd\u7684\u6210\u672c\u5728\u964d\u4f4e\u3002\u8fd9\u662f\u56e0\u4e3a\u8f6f\u4ef6\u4f9d\u8d56\u4e8e\u7b97\u529b\uff0c\u800c\u7b97\u529b\u5728\u4eca\u5929\u4f9d\u7136\u6602\u8d35\u3002","date":"2023-11-17T00:00:00.000Z","formattedDate":"2023\u5e7411\u670817\u65e5","tags":[{"label":"Yuan","permalink":"/zh-Hans/blog/tags/yuan"},{"label":"Tech","permalink":"/zh-Hans/blog/tags/tech"}],"readingTime":2.97,"hasTruncateMarker":false,"authors":[{"name":"CZ","title":"NTNL Co-Founder","url":"https://github.com/zccz14","imageURL":"https://github.com/zccz14.png","key":"zccz14"}],"frontMatter":{"slug":"do-we-really-need-python-for-quant","title":"\u6211\u4eec\u771f\u7684\u9700\u8981 Python \u6765\u505a\u91cf\u5316\u4ea4\u6613\u5417\uff1f","authors":["zccz14"],"tags":["Yuan","Tech"]},"unlisted":false,"prevItem":{"title":"No Trade No Life \u7684\u613f\u666f","permalink":"/zh-Hans/blog/vision-of-no-trade-no-life"}},"content":"\u5982\u4eca\uff0cPython \u662f\u91cf\u5316\u4ea4\u6613\u4e2d\u6700\u6d41\u884c\u7684\u7f16\u7a0b\u8bed\u8a00\u3002VNPY\u3001Zipline\u3001PyAlgoTrade \u548c Backtrader \u90fd\u662f\u7528 Python \u7f16\u5199\u7684\u3002Python \u5728\u91cf\u5316\u4ea4\u6613\u4e2d\u88ab\u5e7f\u6cdb\u4f7f\u7528\uff0c\u4f46\u53ef\u80fd\u5e76\u975e\u6700\u9002\u5408\u8fd9\u9879\u5de5\u4f5c\u7684\u8bed\u8a00\u3002\u867d\u7136\u5b83\u6709\u5f88\u591a\u4f18\u70b9\uff0c\u6bd4\u5982\u6613\u4e8e\u5b66\u4e60\u548c\u62e5\u6709\u51fa\u8272\u7684\u6570\u636e\u79d1\u5b66\u751f\u6001\u7cfb\u7edf\uff0c\u4f46\u91cf\u5316\u6295\u8d44\u5e73\u53f0\u5982 Quantopian\u3001JoinQuant \u548c QuantConnect \u5374\u96be\u4ee5\u76c8\u5229\uff0c\u5c3d\u7ba1\u4e92\u8054\u7f51\u57fa\u7840\u8bbe\u65bd\u7684\u6210\u672c\u5728\u964d\u4f4e\u3002\u8fd9\u662f\u56e0\u4e3a\u8f6f\u4ef6\u4f9d\u8d56\u4e8e\u7b97\u529b\uff0c\u800c\u7b97\u529b\u5728\u4eca\u5929\u4f9d\u7136\u6602\u8d35\u3002\\n\\n\u90e8\u5206\u5e73\u53f0\u63d0\u4f9b\u4e86\u672c\u5730\u5b89\u88c5\u7684\u80fd\u529b\uff0c\u8fd9\u53ef\u80fd\u662f\u4e00\u4e2a\u66f4\u7ecf\u6d4e\u7684\u9009\u62e9\uff0c\u4f46\u5bf9\u975e\u4e13\u4e1a\u7684\u4ea4\u6613\u8005\u6765\u8bf4\uff0c\u5b89\u88c5\u548c\u73af\u5883\u914d\u7f6e\u53ef\u80fd\u4f1a\u6210\u4e3a\u4e00\u4e2a\u95ee\u9898\u3002\u4e3a\u4e86\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\uff0c\u5e73\u53f0\u901a\u5e38\u4e3a\u7528\u6237\u63d0\u4f9b\u4e86 Web GUI\u3002\u51b5\u4e14\u672c\u5730\u5b89\u88c5\u7684\u8f6f\u4ef6\u4e0d\u5177\u5907\u8de8\u5e73\u53f0\u6027\uff0c\u65e0\u6cd5\u5229\u7528\u4e91\u8ba1\u7b97\u80fd\u529b\uff0c\u800c\u4e14\u5f88\u96be\u5b9e\u73b0\u9ad8\u53ef\u7528\u3002\\n\\n\u91cf\u5316\u4ea4\u6613\u9700\u8981\u5927\u91cf\u7684\u7814\u7a76\u548c\u5f00\u53d1\uff0c\u800c\u5728\u6a21\u578b\u7814\u7a76\u6d4b\u8bd5\u9636\u6bb5\u9700\u8981\u5927\u91cf\u7684\u7b97\u529b\uff0c\u6b64\u65f6\uff0c\u4e91\u8ba1\u7b97\u65e0\u8bba\u5bf9\u4e8e\u7528\u6237\u8fd8\u662f\u5e73\u53f0\u6765\u8bf4\u53ef\u80fd\u90fd\u662f\u4e00\u4e2a\u8fc7\u4e8e\u6602\u8d35\u7684\u9009\u62e9\u3002\u5982\u679c\u7528\u6237\u5e76\u4e0d\u786e\u4fe1\u4ed6\u4eec\u80fd\u4ece\u5e73\u53f0\u8d5a\u94b1\uff0c\u4ed6\u4eec\u5c31\u4e0d\u4f1a\u4e3a\u6b64\u4ed8\u8d39\u3002\\n\\n\u56e0\u6b64\uff0c\u9488\u5bf9\u8fd9\u4e9b\u95ee\u9898\uff0c\u6211\u4eec\u63d0\u51fa\u4e86\u4e00\u4e2a\u89e3\u51b3\u65b9\u6848\uff1a\u7528 JavaScript \u66ff\u4ee3 Python\u3002JavaScript \u53ef\u4ee5\u5728\u6d4f\u89c8\u5668\u4e2d\u4f7f\u7528\uff0c\u5141\u8bb8\u7528\u6237\u5229\u7528\u81ea\u5df1\u8bbe\u5907\u7684\u8ba1\u7b97\u80fd\u529b\uff0c\u65e0\u9700\u8fdb\u884c\u672c\u5730\u5b89\u88c5\u3002JavaScript \u751f\u6001\u7cfb\u7edf\u6210\u719f\u4e14\u4e0d\u65ad\u53d1\u5c55\uff0c\u4f7f\u5176\u6210\u4e3a\u6700\u9002\u5408 Web \u5f00\u53d1\u548c\u6570\u636e\u79d1\u5b66\u7684\u751f\u6001\u7cfb\u7edf\u3002\u800c\u4e14 JavaScript \u6bd4 Python \u66f4\u5feb\u3002\\n\\n\u6211\u4eec\u5df2\u7ecf\u4f7f\u7528 JavaScript \u6784\u5efa\u4e86\u4e00\u4e2a\u91cf\u5316\u5e73\u53f0\uff0c\u53ef\u4ee5\u5728\u6d4f\u89c8\u5668\u548c\u4e91\u670d\u52a1\u5668\u4e0a\u8fd0\u884c\u3002\u5728\u6d4f\u89c8\u5668\u4e2d\uff0c\u7528\u6237\u53ef\u4ee5\u5b8c\u5168\u514d\u8d39\u8fdb\u884c\u7814\u7a76\u548c\u5f00\u53d1\uff0c\u76f4\u5230\u4ed6\u4eec\u60f3\u8981\u5728\u4e91\u670d\u52a1\u5668\u4e0a\u8fd0\u884c\u6a21\u578b\u3002\u8fd9\u6837\uff0c\u7528\u6237\u53ef\u4ee5\u5728\u652f\u4ed8\u8d39\u7528\u4e4b\u524d\u4ece\u5e73\u53f0\u4e2d\u83b7\u5f97\u4ef7\u503c\uff0c\u800c\u5e73\u53f0\u5728\u6536\u5230\u4ed8\u6b3e\u540e\u53ef\u4ee5\u652f\u4ed8\u8ba1\u7b97\u80fd\u529b\uff0c\u5f62\u6210\u4e00\u79cd\u53cc\u8d62\u7684\u89e3\u51b3\u65b9\u6848\u3002\\n\\n\u672a\u6765\uff0c\u6211\u4eec\u5c06\u5206\u4eab\u5982\u4f55\u5728\u6ca1\u6709\u6210\u672c\u7684\u60c5\u51b5\u4e0b\u5b58\u50a8\u548c\u5206\u53d1\u6570\u636e\uff0c\u4ee5\u786e\u4fdd\u5e73\u53f0\u59cb\u7ec8\u8fd0\u884c\u3002\u6709\u4e86\u8fd9\u4e9b\u4f18\u52bf\uff0c\u6211\u4eec\u786e\u4fe1 JavaScript \u662f\u672a\u6765\u91cf\u5316\u4ea4\u6613\u7684\u6700\u4f73\u9009\u62e9\u3002"}]}')}}]);